-- Populate institutions table with federal institutions

-- Insert default institution for Infomar courses
ON CONFLICT (name) DO NOTHING;

-- Insert federal institutions with STANDARD workload rules
-- Standard rules: 15=65h, 30=130h, 45=195h, 60=260h, 75=295h, 90=390h
ON CONFLICT (name) DO NOTHING;

-- Insert institutions with SPECIAL workload rules (Tribunais Regionais do Trabalho)
-- TRT rules: 15=40h, 30=80h, 45=120h, 60=160h, 75=200h, 90=240h
ON CONFLICT (name) DO NOTHING;

-- Update existing courses without institution to use default Infomar rules
UPDATE public.courses
SET institution_id = (
  SELECT id FROM public.institutions WHERE name = 'Regras Padrão (Infomar)' LIMIT 1
)
WHERE institution_id IS NULL;
